import React from 'react';
import { useParams } from 'react-router-dom';
import items from './ItemList';

export default function DescriptionOfItems() {
  // Get the item id from the URL
  const { id } = useParams();

  // Find the item based on the id parameter
  const item = items.find((item) => item.id === parseInt(id));

  if (!item) {
    return <h2>Item not found</h2>;
  }

  return (
    <div style={{ padding: '20px', textAlign: 'center' }}>
      {/* Display the Item Image */}
      <img src={item.url} alt={item.name} style={{ maxWidth: '500px', height: 'auto' }} />

      {/* Item Basic Information */}
      <h1>{item.name}</h1>
      <p style={{ fontSize: '18px' }}>
        <strong>Price:</strong> â‚¹{item.price}
      </p>
      <p style={{ fontSize: '16px', margin: '10px 0' }}>{item.description}</p>

      {/* Display Specifications if available */}
      {item.specifications && (
        <div style={{ marginTop: '20px', textAlign: 'left', display: 'inline-block' }}>
          <h2>Specifications</h2>
          <table
            style={{
              border: '1px solid #ccc',
              borderCollapse: 'collapse',
              width: '80%',
              margin: '0 auto',
              textAlign: 'left',
            }}
          >
            <tbody>
              {Object.entries(item.specifications).map(([key, value]) => (
                <tr key={key}>
                  <td
                    style={{
                      border: '1px solid #ddd',
                      padding: '10px',
                      fontWeight: 'bold',
                      backgroundColor: '#f9f9f9',
                    }}
                  >
                    {key.charAt(0).toUpperCase() + key.slice(1)}:
                  </td>
                  <td style={{ border: '1px solid #ddd', padding: '10px' }}>
                    {Array.isArray(value) ? value.join(', ') : value}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
