import React, { useState } from 'react';
import './About.css';

const QueryForm = () => {
   const [name,setname] = useState(userdata.user.name)
   const [email,setEmail] = useState()
   const [subject,setsubject] = useState()
   const [message,setmessage] = useState()

   const userdata = localStorage.getItem("userdata")

   if(userdata){
    setname(userdata.user.name)
   }

   function handleSubmit(){
    alert("hello")
   }

  return (
    <div className='queryform'>
      <h2>Contact Us</h2>
      <form>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={name}
            onChange={(e)=>{
                setname(e.target.value)
            }}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={email}
            onChange={(e)=>{
                setEmail(e.target.value)
            }}
            required
          />
        </div>
        <div>
          <label>Subject:</label>
          <input
            type="text"
            name="subject"
            value={subject}
            onChange={(e)=>{
               setsubject(e.target.value)
            }}
            required
          />
        </div>
        <div>
          <label>Message:</label>
          <textarea
            name="message"
            value={message}
            onChange={(e)=>{
                setmessage(e.target.value)
            }}
            required 
            rows={10}
            cols={40}
          />
        </div>
        <button type="submit" onClick={()=>{
            handleSubmit()
        }}>Submit</button>
      </form>
    </div>
  );
};

export default QueryForm;
