import React, { useState } from 'react';
import './App.css';
import Content from './Content.js';
//import Footer from './Footer.js';
import NavBar from './NavBar.js';
import News from './NewsContent.js';
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import About from './About.js';
import Contact from './Contact.js';
import Cart from './Cart.js';
import items from './ItemList.js';
const [modestyle, newmodestyle] = useState("dark");
  const [btnValue, setBtnValue] = useState("Dark Mode");
  const [cart,setCart] = useState([])
  const [text, setText] = useState('');
  const [searchArray,setSearchArray] = useState([])

function searchData() {
  const itemFound = items.findIndex((item) => item.name.toLowerCase() === text.toLowerCase());

  setSearchArray([...searchArray,items[itemFound]])


}



function App() {
  const toggleMode = () => {
    if (modestyle === "dark") {
      newmodestyle('light');
      setBtnValue("Light Mode");
    } else {
      newmodestyle('dark');
      setBtnValue("Dark Mode");
    }
  }


  console.log(cart)
  console.log(searchArray)

  return (
    <>
      <div className='firstParent'>
      <BrowserRouter>
        <NavBar title="MOBileee" Home="Home" mode={modestyle} toggle={toggleMode} value={btnValue}  text={text} setText={setText}/>
        <Routes>
          <Route path="/about" element={<About mode={modestyle}/>} />
          <Route path="/" element={<Content mode={modestyle} cart={cart} setCart={setCart} text={text} searchArray={searchArray} setSearchArray={setSearchArray}/>} />
          <Route path="/contact" element={<Contact mode={modestyle} />} />
          <Route path="/news" element={<News mode={modestyle} />} />
          <Route path="/cart" element={<Cart mode={modestyle}  cart={cart} setCart={setCart}/>} />
        </Routes>

         {/*<Footer about="About Me" mode={modestyle} />*/}
      </BrowserRouter>

      </div>
    </>
  );
}

export default App;
