import React from 'react';
import items from './ItemList';
import './Content.css';

export default function Content(props) {
  // Define the style based on the mode prop
  const modeStyle = {
    backgroundColor: props.mode === 'dark' ? 'white' : 'grey',
  };

  return (
    <div className='main'>
      <div className='MainContent' id="items" style={modeStyle}>
        {/* Use map to iterate over items and display each item */}
        {items.map((item) => (
          <div key={item.id} className='item'>
            {/* Display the item's details */}
            <h1>{item.name}</h1>
            <p><strong>Price:</strong> â‚¹{item.price}</p>
            <p>{item.description}</p>
            <button>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}
