import NavBar from './NavBar.js';
import { BrowserRouter as Router,Switch,Route,Link } from 'react-router-dom';

function App() {
  const toggleMode = ()=>{
    if(modestyle==="dark"){
      newmodestyle('light')

      setBtnValue("Light Mode")
    }
    else{
      newmodestyle('dark')

      setBtnValue("Dark Mode")
    }

  }

  const [modestyle,newmodestyle] = useState("dark")
  const [btnValue,setBtnValue] = useState("Dark Mode")
  return (
    <>
    <route>
    <NavBar title="ankushDHULL" Home="Home" mode={modestyle} toggle={toggleMode} value={btnValue}/>
    <Content mode={modestyle}/>
    <Footer about="About Me" mode={modestyle}/>
    </route>
    </>
  );
}

export default App;
