import React, { useState } from 'react';
import './App.css';
import Content from './Content.js';
import Footer from './Footer.js';
import NavBar from './NavBar.js';
import News from './NewsContent.js';
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import About from './About.js';
import Contact from './Contact.js';

function App() {
  const toggleMode = () => {
    if (modestyle === "dark") {
      newmodestyle('light');
      setBtnValue("Light Mode");
    } else {
      newmodestyle('dark');
      setBtnValue("Dark Mode");
    }
  }

  const [modestyle, newmodestyle] = useState("dark");
  const [btnValue, setBtnValue] = useState("Dark Mode");
  const [cart,setCart] = useState([])
  console.log(cart)

  return (
    <>
      <BrowserRouter>
        <NavBar title="ankushDHULL" Home="Home" mode={modestyle} toggle={toggleMode} value={btnValue}/>
        <Routes>
          <Route path="/about" element={<About mode={modestyle}/>} />
          <Route path="/" element={<Content mode={modestyle} cart={cart} setCart={setCart}/>} />
          <Route path="/contact" element={<Contact mode={modestyle} />} />
          <Route path="/news" element={<News mode={modestyle} />} />
          <Route path="/cart" element={<Cart mode={modestyle}  cart={cart}/>} />
        </Routes>
        <Footer about="About Me" mode={modestyle} />
      </BrowserRouter>
    </>
  );
}

export default App;
