import React ,{useState}from 'react'

export default function Content() {



    const fun = ()=>{
          let newText = text.toUpperCase()
          setText(newText)
    }

    const changeFunction =(event)=>{
        setText(event.target.value)
        
    }

    const toggleBtn = ()=>{
      if(btnText==="Dark Mode"){
        setNewStyle({
          backgroundColor:"black",
          color:"white"
        })
        BtnSetText("Light Mode")
      }
      else{
        setNewStyle({
          backgroundColor:"white",
          color:"black"
        })
        BtnSetText("Dark Mode")
        setNewbtnbackground({
          backgroundColor : "white",
          color:"white"
        })
      }
      
    }

    const [text,setText] = useState("Enter Text")
    const [btnText,BtnSetText] = useState("Dark Mode")
    const [mystle,setNewStyle] = useState({
      backgroundColor : "white",
      color:"black"
    })
    const [btnbackground,setNewbtnbackground] = useState({
      backgroundColor : "black",
      color:"white"
    })
  return (
    <div className='main' style={mystle}>
      <div className="dark-light-mode">
       <button className='darkbtn' onClick={toggleBtn}  style={btnbackground}>{btnText}</button>
      </div>
    <div className='MainContent'>
    <textarea value={text} cols={50} rows={8} onChange={changeFunction}/>
    <button className="cnvrtBtn" onClick={fun}>Convert To UpperCase</button>
    </div>
    
    </div>
    
  )
}
