import React from 'react';
import './Cart.css';

export default function Cart(props) {

    const modeStyle = {
        backgroundColor: props.mode === 'dark' ? 'white' : 'grey',
      };
  // Create a copy of the cart
  var updatedCart = [...props.cart];

  // Function to decrease the quantity of an item or remove it if quantity is 1
  function minsQuantity(index) {
    if (updatedCart[index].quantity > 1) {
      // Decrease the quantity by 1
      updatedCart[index].quantity -= 1;
    } else {
      // Remove the item from the cart if quantity is 1
      updatedCart.splice(index, 1);
    }

    // Update the cart state in the parent component
    props.setCart(updatedCart);
  }

  return (
    <div className='Cart' style={modeStyle}>
      {props.cart.length === 0 ? (
        <p>CART EMPTY</p>
      ) : (
        props.cart.map((item, index) => (
          <div key={item.id} className='item2' >
            <h1>{item.name}</h1>
            <p><strong>Price:</strong> â‚¹{item.price}</p>
            <p>{item.description}</p>
            <p><strong>Quantity:</strong> {item.quantity}</p>
            <button onClick={() => minsQuantity(index)}> - </button>
            <button onClick={() => minsQuantity(index)}> - </button>
          </div>
        ))
      )}
    </div>
  );
}
