import React from 'react';
import './Cart.css';
import { useNavigate } from 'react-router-dom';

export default function Cart(props) {

  const navigate = useNavigate()
  var totalcartprice = 0;
  var totalPrice = 0;

    const modeStyle = {
        backgroundColor: props.mode === 'dark' ? 'white' : 'grey',
      };
  
  var updatedCart = [...props.cart];

  function CartTotalPrice()
  {
    totalPrice+= updatedCart.map((item)=>{
      return(item.totalPrice) 
    })

    

    

  }

  


  function minsQuantity(index) {
    if (updatedCart[index].quantity > 1) {
     
      updatedCart[index].quantity -= 1;
      
    } else {
      
      updatedCart.splice(index, 1);
    }

  
    props.setCart(updatedCart);
  }



  function PlusQuantity(index){
    updatedCart[index].quantity +=1;
    totalPrice = updatedCart[index].quantity * updatedCart[index].price;
    console.log("TOTAL PRICE"+totalPrice)
    props.setCart(updatedCart);
   
  
  }


  function removeFromCart(index){
   
    updatedCart.splice(index,1)
    props.setCart(updatedCart)

  }

  



  return (
    <div className='Cart' style={modeStyle}>

      {props.cart.length === 0 ? (
        <p>CART EMPTY</p>
      ) : (
        props.cart.map((item, index) => (
          <div key={item.id} className='item2' >
            <img src={item.url}  onClick={() => navigate(`/items/${item.id}`)} alt='hi'/>
            <h1 onClick={() => navigate(`/items/${item.id}`)}>{item.name} </h1>
            <p><strong>Price:</strong> ₹{item.price}</p>
            <p>{item.description}</p>
            <p><strong>Quantity:</strong> {item.quantity}</p>
            <p><strong>Total Price:</strong> ₹{item.quantity*item.price}</p>
            <button id="first" onClick={() => minsQuantity(index)}> - </button>
            <button id="second" onClick={() => 
              PlusQuantity(index)}> + </button>
              <button id="remove" onClick={()=>{removeFromCart(index)}}>Remove From Cart</button>
          </div>
          
        ))
      )}
    </div>
  );
}
