import React, { useEffect, useState } from 'react';
import { useParams,useNavigate } from 'react-router-dom';
import './DescriptionOfItems.css'; // Import the CSS file

export default function DescriptionOfItems(props) {
  const modeStyle = {
    backgroundColor: props.mode === 'dark' ? 'white' : 'grey',
  };

  const [comments, setComments] = useState([]); // State to store comments

  

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await fetch("http://localhost:7600/allcomments", {
          method: "GET",
          headers: {
            'Content-Type': 'application/json',
          }
        });

        // Check if the response is okay
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const result = await response.json(); // Convert the response to JSON
        setComments(result); // Set the comments state with the fetched data
        console.log(result); // Log the fetched data
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
      }
    };

    setComments(...comments,fetchComments()) // Call the async function to fetch data when the component is mounted
  }, []);
  

  const navigate = useNavigate()

  const token = localStorage.getItem("userdata")

  const username = JSON.parse(token)

  const { id } = useParams();
  const item = props.allItems.find((item) => item.id === parseInt(id));

  
  const [newComment, setNewComment] = useState(''); // State to store new comment

  if (!item) {
    return <h2>Item not found</h2>;
  }

  const handleAddToCart = () => {
    let CopyOfCart = [...props.cart]; // Create a copy of the cart
    let ExistingItemIndex = CopyOfCart.findIndex((Cartitem) => {
      return Cartitem.id === item.id; // Check for the item's existence
    });

    if (ExistingItemIndex !== -1) { // Check if item exists
      CopyOfCart[ExistingItemIndex].quantity += 1; // Increase quantity
      props.setCart(CopyOfCart); // Update the cart state
    } else {
      const newItem = { ...item, quantity: 1 }; // Start quantity at 1
      props.setCart([...CopyOfCart, newItem]); // Add new item to cart
    }
  };

  function handleSubmit(e){
    e.preventDefault()
    const commentData = {
      name: username.user.name,
      itemId : id,
      comment:newComment
    }

    console.log(commentData)


    try{
         fetch("http://localhost:7600/comment",
          {
            method:"POST",
            headers: {
              'Content-Type': 'application/json',
          },
            body: JSON.stringify(commentData)
          }

         )
    }catch(error){

    }
  }

  // Handle comment submission
  
  return (
    <div className="container" style={modeStyle}>
    
      <img src={item.Bigimage} alt={item.name} className="item-image" />
       <div className="item-details">
        <h1 className="item-title">{item.name}</h1>
        <p className="item-price"><strong>Price:</strong> â‚¹{item.price}</p>
        <p className="item-description">{item.description}</p>
        <button className="add-to-cart" onClick={handleAddToCart}>
          Add to Cart
        </button>

        {item.specifications && (
          <div className="specifications">
            <h2>Specifications</h2>
            <table className="specifications-table">
              <tbody>
                {Object.entries(item.specifications).map(([key, value]) => (
                  <tr key={key}>
                    <td>{key.charAt(0).toUpperCase() + key.slice(1)}:</td>
                    <td>{Array.isArray(value) ? value.join(', ') : value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
    

        {/* Comment Section */}
        
      </div>
      <div className="comments-section">
          <h2>Comments</h2>
          
          {/* Display comments */}
          <div className="comments-list">
            {comments.length > 0 ? (
              comments.map((comment, index) => (
                <div key={index} className="comment">
                  <p>{comment}</p>
                </div>
              ))
            ) : (
              <p>No comments yet.</p>
            )}
          </div>

          {/* Comment Form */}
          <form className="comment-form">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add a comment"
              rows={10}
              cols={50}
              required
            />
            <button type="submit" onClick={(e)=>{
              if(!username){
                alert("login First")
                navigate("/form")
              }else{
                handleSubmit(e)
                
              }
            }}>Submit Comment</button>
          </form>
        </div>
    </div>
  );
}
