import './Hero1.css';

const Hero1 = (props) => {

  console.log(props.filterItems)

  // Move fetchVideo inside useEffect and remove the direct call from the component body
  const fetchVideo = async () => {
    try {
      const res = await fetch('/api/items/25');
      const data = await res.json();
      console.log(data);
    } catch (error) {
      console.log(error);
    }
  };

  // Use useEffect to handle side effects (like fetching data)
  useEffect(() => {
   // fetchVideo(); 
  }, []); // Empty dependency array to run it once on mount

  return (
    <div className='hero'>
      <video alt="hello" src="/computer2.mp4" autoPlay loop muted />
    </div>
  );
};

export default Hero1;
