import React, { useState, useEffect } from 'react';
import './Content.css';
import Hero1 from './Hero1.js';
import { useNavigate } from 'react-router-dom';

export default function Content(props) {
    const [selected,setSelected] = useState("")
    const navigate = useNavigate();
    const [filterItems, setFilterItems] = useState([]);
   // State to hold all items

    // Define the style based on the mode prop
    const modeStyle = {
        backgroundColor: props.mode === 'dark' ? 'white' : 'grey',
    };

    // Fetch items from the API
    const fetchItems = async () => {
        try {
            const response = await fetch('http://localhost:7600/api/items'); // Adjust the URL as needed
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            console.log(data)
            props.setAllItems(data); // Store the fetched items
            setFilterItems(data); // Set initial filtered items
        } catch (error) {
            console.error('Error fetching items:', error);
        }
    };

    useEffect(() => {
        fetchItems(); // Fetch items when the component mounts
    }, []);

    function AddToCart(index) {
        props.setCartCount(props.cartCount+1)
        const itemToAdd = filterItems[index];
        const existingItem = props.cart.find((cartItem) => cartItem.id === itemToAdd.id);

        if (existingItem) {
            const updatedCart = props.cart.map((cartItem) =>
                cartItem.id === itemToAdd.id
                    ? { ...cartItem, quantity: cartItem.quantity + 1 }
                    : cartItem
            );
            props.setCart(updatedCart);
        } else {
            props.setCart([...props.cart, { ...itemToAdd, quantity: 1 }]);
            
        }
    }

    function phones() {
        setSelected("active")
        const filteredPhones = props.allItems.filter(i => i.category === "phone");
        setFilterItems(filteredPhones);
    }

    function laptops() {
        setSelected("active")
        const filteredLaptops = props.allItems.filter(i => i.category === "laptop");
        setFilterItems(filteredLaptops);
    }

    function all() {
        setSelected("active")
        setFilterItems(props.allItems); // Reset filterItems to the full list
    }

    function television() {
        setSelected("active")
        const filteredTVs = props.allItems.filter(i => i.category === "tv");
        setFilterItems(filteredTVs);
    }

    return (
        <div className='main' style={modeStyle}>
            <Hero1 style={modeStyle} />
            <div className='Categories'>
                <h1>Categories</h1>
                <img className={selected} src="/asus.jpeg" onClick={all} alt="All" />
                <img className={selected==="active"?"":"active"} src="/asus.jpeg" onClick={laptops} alt="Laptops" />
                <img className={selected==="active"?"":"active"} src="/s24.jpg" onClick={phones} alt="Phones" />
                <img className={selected==="active"?"":"active"} src="/qled.jpeg" onClick={television} alt="Television" />
            </div>

            <div className='MainContent' id="items" style={modeStyle}>
                {filterItems.map((item, index) => (
                    <div key={item.id} className='item' style={modeStyle}>
                        <img src={item.url} onClick={() => navigate(`/items/${item.id}`)} alt={item.name} />
                        <h1 onClick={() => navigate(`/items/${item.id}`)}>{item.name}</h1>
                        <p><strong>Price:</strong> â‚¹{item.price}</p>
                        <p>{item.description}</p>
                        <button onClick={() => AddToCart(index)}>Add to Cart</button>
                    </div>
                ))}
            </div>
        </div>
    );
}
