import React from 'react'
//import img1 from './public/react.jpg'

export default function About(props) {
    const mystyle = {
        backgroundColor: props.mode==="dark" ? "white":"grey"
    }
  return (
    <div className="AboutPage" style={mystyle}>
      <img src="/react.jpg"/>
    </div>
  )
}
