import React from 'react';
import { useLocation } from 'react-router-dom';
import items from './ItemList'; // Import your items list
import './Search1.css'

export default function Search(props) {
  const location = useLocation();
  const { searchQuery } = location.state || ""; // Retrieve search query from the state

  // Filter items based on the search query
  const filteredItems = items.filter((item) =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())

  
  );

  

  function AddToCart(index){

    const foundInd = props.cart.findIndex((cartItem)=>{
      cartItem.id===items[index].id;
    })
    props.setCart([...props.cart,items[index]]);
    
  }

  return (
    <div className="search-container">
      <h2>Search Results for: "{searchQuery}"</h2>
      {filteredItems.length > 0 ? (
        <div className="results">
          {filteredItems.map((item, index) => (
            
            <div key={item.id} className="item-card">
              
              <img src={item.url} alt={item.name} />
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p>Price: â‚¹{item.price}</p>
              <button onClick={()=>{
                AddToCart(index)
              }}>Add To Cart</button>
            </div>
          ))}
        </div>
      ) : (
        <p>No items found matching your search criteria.</p>
      )}
    </div>
  );
  
}
