import React, { useState } from 'react';
import './App.css';
import Content from './Content.js';
import Footer from './Footer.js';
import NavBar from './NavBar.js';
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import About from './About.js';
import Contact from './Contact.js';

function App() {
  const toggleMode = () => {
    if (modestyle === "dark") {
      newmodestyle('light');
      setBtnValue("Light Mode");
    } else {
      newmodestyle('dark');
      setBtnValue("Dark Mode");
    }
  }

  const [modestyle, newmodestyle] = useState("dark");
  const [btnValue, setBtnValue] = useState("Dark Mode");

  return (
    <>
      <BrowserRouter>
        <NavBar title="ankushDHULL" Home="Home" mode={modestyle} toggle={toggleMode} value={btnValue}/>
        <Routes>
          <Route path="/about" element={<About mode={modestyle}/>} />
          <Route path="/" element={<Content mode={modestyle} />} />
          <Route path="/contact" element={<Contact mode={modestyle} />} />
        </Routes>
        <Footer about="About Me" mode={modestyle} />
      </BrowserRouter>
    </>
  );
}

export default App;
